ze < 5000000) {                $distination_img = $upload_dir . 'poster_' . $id_image . '.' . $imgExt;                if (file_exists($distination_img)) {                    unlink($distination_img);                }                if (!move_uploaded_file($tmp_dir, $distination_img)) {                    $controle = FALSE;                    //echo 'entre-2';                }            }        }    }    return $controle;}/* * ***************************************************************************************************** */function creerFichierStrm($fichierChemin, $fichierNom, $fichierExtension, $fichierContenu, $droit = "") {    $fichierCheminComplet = $fichierChemin . $fichierNom . $fichierExtension;// création du fichier sur le serveur    $leFichier = fopen($fichierCheminComplet, "wb");    fwrite($leFichier, $fichierContenu);    fclose($leFichier);// la permission    if ($droit == "") {        $droit = 0777;    }// on vérifie que le fichier a bien été créé    $t_infoCreation['fichierCreer'] = false;    if (file_exists($fichierCheminComplet) == true) {        $t_infoCreation['fichierCreer'] = true;    }// on applique les permission au fichier créé    $retour = chmod($fichierCheminComplet, $droit);    // var_dump($retour);    $t_infoCreation['permissionAppliquer'] = $retour;    return $t_infoCreation;}//ftp://achachia2003:7130chachia@pool182.seedbox.fr:21/VOD/FilmsArabic/Ahwak.HD.720p.mkv//dav://myvod69800%40netcmail.com:myvod69800@webdav.4shared.com:80/Jupiter.Ascending.avi// recuperation des informations de la source et compte$etat = TRUE;$objet = array();/* * **************** Recuperer url serveur ****************************************** */try {    $sql = " SELECT url_serveur FROM  ListeServeursVod  WHERE  id_serveur=:param";    $stmt = $cxn->prepare($sql);    $stmt->bindParam(':param', $serveur_film);    $stmt->execute();    $enregistrement = $stmt->fetch();    $