<?php/* * ************* Verification une date ************** */function validateDate($date, $format = 'Y-m-d H:i:s') {    $d = DateTime::createFromFormat($format, $date);    return $d && $d->format($format) == $date;}function liste_SectionVod($id_categorie) {    global $cxn;    $liste = array();    try {        // requete prepare        $sql = " SELECT id_section,nom_section FROM SectionVod  WHERE id_categorie=:param  ";        $resultat = $cxn->prepare($sql);        $resultat->bindParam(':param', $id_categorie);        $resultat->execute();        $i = 0;        while ($enregistrement = $resultat->fetch()) {            $liste[$i]['id_section'] = $enregistrement['id_section'];            $liste[$i]['nom_section'] = html_entity_decode($enregistrement['nom_section']);            $i++;        }    } catch (Exception $e) {        echo "Une erreur est survenue lors de la récupération des données";    }    return $liste;}function creerFichier($fichierChemin, $fichierNom, $fichierExtension, $fichierContenu, $droit = "") {    //$fichierCheminComplet = $_SERVER["DOCUMENT_ROOT"] . $fichierChemin . "/" . $fichierNom;    $fichierCheminComplet = $_SERVER["DOCUMENT_ROOT"] . $fichierChemin . "/" . $fichierNom;    //echo $fichierCheminComplet;    if ($fichierExtension != "") {        $fichierCheminComplet = $fichierCheminComplet . "." . $fichierExtension;        //$t_infoCreation['infos']=$fichierCheminComplet;    }// création du fichier sur le serveur    $leFichier = fopen($fichierCheminComplet, "wb");    fwrite($leFichier, $fichierContenu);    fclose($leFichier);// la permission    if ($droit == "") {        $droit = "0777";    }// on vérifie que le fichier a bien été créé    $t_infoCreation['fichierCreer'] = false;    if (file_exists($fichierCheminComplet) == true) {        $t_infoCreation['fichierCreer'] = true;    }// on applique les permission au fichier créé    $retour = chmod($fichierCheminComplet, intval($droit, 8));    $t_infoCreation['permissionAppliquer'] = $retour;    return $t_infoCreation;}function getNbr_films() {    $dir = "/volume1/web/media/Films-HD";    $dh = opendir($dir);    $nbr = 0;    while (false !== ($filename = readdir($dh))) {        if ($filename != '.' && $filename != '..') {            $nbr++;        }    }    return $nbr;}function getNbr_cartoon() {    $dir = "/volume1/web/media/Cartoon";    $dh = opendir($dir);    $nbr = 0;    while (false !== ($filename = readdir($dh))) {        if ($filename != '.' && $filename != '..') {            $nbr++;        }    }    return $nbr;}function getNbr_serie_tv() {    $dir = "/volume1/web/media/Serie-Tv";    $cdir = scandir($dir);    $nbr = 0;    foreach ($cdir as $key => $value) {        if (!in_array($value, array(".", ".."))) {            if (is_dir($dir . DIRECTORY_SEPARATOR . $value)) {                $nbr++;            }        }    }    return $nbr;}function FileSizeConvert($bytes) {    $bytes = floatval($bytes);    $arBytes = array(        0 => array(            "UNIT" => "TB",            "VALUE" => pow(1024, 4)        ),        1 => array(            "UNIT" => "GB",            "VALUE" => pow(1024, 3)        ),        2 => array(            "UNIT" => "MB",            "VALUE" => pow(1024, 2)        ),        3 => array(            "UNIT" => "KB",            "VALUE" => 1024        ),        4 => array(            "UNIT" => "BY",            "VALUE" => 1        ),    );    foreach ($arBytes as $arItem) {        if ($bytes >= $arItem["VALUE"]) {            $result = $bytes / $arItem["VALUE"];            $result = str_replace(".", ",", strval(round($result, 2))) . " " . $arItem["UNIT"];            break;        }    }    return $result;}function getDataFilms() {    $data = 0;    $dir = "/volume1/web/media/Films-HD";    $dh = opendir($dir);    while (false !== ($filename = readdir($dh))) {        if ($filename != '.' && $filename != '..') {            $data = $data + filesize($dir . '/' . $filename);        }    }    $data_formate = FileSizeConvert($data);    return $data_formate;}function getDataCartoon() {    $data = 0;    $dir = "/volume1/web/media/Cartoon";    $dh = opendir($dir);    while (false !== ($filename = readdir($dh))) {        if ($filename != '.' && $filename != '..') {            $data = $data + filesize($dir . '/' . $filename);        }    }    $data_formate = FileSizeConvert($data);    return $data_formate;}function dirToArray($dir) {    $result = array();    $cdir = scandir($dir);    foreach ($cdir as $key => $value) {        if (!in_array($value, array(".", ".."))) {            if (is_dir($dir . DIRECTORY_SEPARATOR . $value)) {                $result[$value] = dirToArray($dir . DIRECTORY_SEPARATOR . $value);            } else {                $result[] = $value;            }        }    }    return $result;}function getDataSerieTv() {    $data = 0;    $dir = "/volume1/web/media/Serie-Tv";    $result = dirToArray($dir);    $j = 1;    foreach ($result as $serie => $value) {        foreach ($value as $saison => $value1) {            foreach ($value1 as $num_episode => $nom_episode) {                $filename = $dir . '/' . $serie . '/' . $saison . '/' . $nom_episode;                $info = new SplFileInfo($filename);                $data = $data + filesize($filename);            }        }    }    $data_formate = FileSizeConvert($data);    return $data_formate;}/* Test  $date = '2015-04-01';  if (!validateDate($date, 'Y-m-d')) {  echo 'non valide';  }else{  echo 'valide';  } *//* * *************  chaine de caractere ************** */// function unhtmlentities($string) {//     $string = trim($string);//     $string = (!get_magic_quotes_gpc()) ? addslashes($string) : $string;// 