<?php//modelefunction liste_fichiers() {    global $cxn, $url_espace_admin;    $liste_fichiers = array();    $liste_fichier_enregistre = array();    $liste_fichier_non_enregistre = array();    if ($_SERVER['SERVER_NAME'] == 'localhost') {        $dir = "C:\wamp64\www\MegaTV-Backend\Media-Vod\DocumentaireFr";    } else {        $dir = "/volume1/web/media/DocumentaireFr";    }    $url_affiche = $url_espace_admin . '/images/JaquetteDoc';    $dh = opendir($dir);    $i = 0;    while (false !== ($filename = readdir($dh))) {        if ($filename != '.' && $filename != '..') {            $info = new SplFileInfo($dir . '/' . $filename);            try {                     $sql = " SELECT FichierVod.id_fichier,FichierVod.titre_originale,FichierVod.section_fichier,FichierVod.taille_fichier,FichierVod.id_TMD,FichierVod.genre "                        . " FROM  FichierVod,LinksServersFichierVod"                        . " WHERE FichierVod.id_fichier=LinksServersFichierVod.id_fichier "                        . "  AND    LinksServersFichierVod.nom_fichier='" . $filename . "'   ORDER BY  FichierVod.id_fichier DESC ";                $select = $cxn->query($sql);                $nb = $select->rowCount();                if ($nb <= 0) {                    $liste_fichier_non_enregistre[$i]['nom_fichier'] = substr($info->getBasename($info->getExtension()), 0, -1);                    $liste_fichier_non_enregistre[$i]['nom_fichier_complet'] = $filename;                    $liste_fichier_non_enregistre[$i]['taille_fichier'] = FileSizeConvert(filesize($dir . '/' . $filename));                    $liste_fichier_non_enregistre[$i]['extention_fichier'] = $info->getExtension();                    $liste_fichier_non_enregistre[$i]['RealPath'] = $info->getRealPath();                } else {                    $enregistrement = $select->fetch();                    $liste_fichier_enregistre[$i]['id_fichier'] = $enregistrement['id_fichier'];                           $liste_fichier_enregistre[$i]['nom_fichier_complet'] = $filename;                    if ($enregistrement['taille_fichier'] != '') {                        $liste_fichier_enregistre[$i]['taille_fichier'] = $enregistrement['taille_fichier'];                    } else {                        $liste_fichier_enregistre[$i]['taille_fichier'] = FileSizeConvert(filesize($dir . '/' . $filename));                    }                    $liste_fichier_enregistre[$i]['titre_originale'] = $enregistrement['titre_originale'];                    $liste_fichier_enregistre[$i]['date_upload'] = $enregistrement['date_upload'];                    $liste_fichier_enregistre[$i]['id_TMD'] = $enregistrement['id_TMD'];                                    $liste_fichier_enregistre[$i]['section_fichier'] = getNomSection($enregistrement['section_fichier']);                    $liste_fichier_enregistre[$i]['genre_fichier'] = $enregistrement['genre'];                    $liste_fichier_enregistre[$i]['collection'] = $enregistrement['collection'];                    $liste_fichier_enregistre[$i]['affiche'] = $enregistrement['poster'];                    $liste_fichier_enregistre[$i]['url_affiche'] = $url_affiche . '/' . $enregistrement['poster'];                    /*                     * ******************************************************** */                    $parseHeaders = parseHeaders('https://api.themoviedb.org/3/movie/' . $enregistrement['id_TMD'] . '?api_key=cf673ba3b2a3baceeeefa90d7460cd10&language=fr');                    $liste_fichier_enregistre[$i]['messageStatutCheckJson'] = getMessageStatut($parseHeaders);                                     /*                     * ******************************************************* */                                                       }            } catch (Exception $e) {                echo $e->getMessage();            }            $i++;        }    }    $liste_fichiers['liste_fichier_enregistre'] = $liste_fichier_enregistre;    $liste_fichiers['liste_fichier_non_enregistre'] = $liste_fichier_non_enregistre;    return $liste_fichiers;}function getNomSection($id_section) {    global $cxn;    try {        $sql = " SELECT nom_section FROM  SectionVod   WHERE id_section=:param ";        $resultat = $cxn->prepare($sql);        $resultat->bindParam(':param', $id_section);        $resultat->execute();        $enregistrement = $resultat->fetch();        $string = $enregistrement['nom_section'];    } catch (Exception $e) {        echo $e->getMessage();    }    return $string;}function getListeGenreFilms() {    global $cxn;    $liste = array();    try {        $sql = " SELECT id_section,nom_section FROM  SectionVod  ";        $resultat = $cxn->prepare($sql);        $resultat->execute();        $i = 0;        while ($enregistrement = $resultat->fetch()) {            $liste[$i]['id_section'] = $enregistrement['id_section'];            $liste[$i]['nom_section'] = $enregistrement['nom_section'];            $i++;        }    } catch (Exception $e) {        echo $e->getMessage();    }    return $liste;}function getListeGenreDocs() {    global $cxn;    $liste = array();    try {        $sql = " SELECT   nom    FROM   genresVideoDocs  ";        $resultat = $cxn->prepare($sql);        $resultat->execute();        $i = 0;        while ($enregistrement = $resultat->fetch()) {            $liste[$i]['nom_genre'] = $enregistrement['nom'];            $i++;        }    } catch (Exception $e) {        echo $e->getMessage();    }    return $liste;}function listeServeursVod() {    global $cxn;    $liste = array();    try {        $sql = " SELECT  id_serveur,url_serveur,emplacement,nom_serveur  FROM  ListeServeursVod   ";        $resultat = $cxn->prepare($sql);        $resultat->execute();        $i = 0;        while ($enregistrement = $resultat->fetch()) {            $liste[$i]['id_serveur'] = $enregistrement['id_serveur'];            $liste[$i]['url_serveur'] = $enregistrement['url_serveur'];            $liste[$i]['emplacement_serveur'] = $enregistrement['emplacement'];            $liste[$i]['nom_serveur'] = $enregistrement['nom_serveur'];            $i++;        }    } catch (Exception $e) {        echo $e->getMessage();    }    return $liste;}?>